/* eslint-disable import/prefer-default-export */
import styled, { css } from 'styled-components';

export const WrapperInput = styled.div`
  padding: 0 32px;
  font-size: 14px !important;
`;

export const WrapperCarouselList = styled.div`
`;

export const WrapperFliker = styled.div`
  padding: 36px;
  position: relative;
  width: 100%;

  .card {
    width: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
  }

  .avatar {
  width: 68px;
  height: 68px;
  border-radius: 50%;
  background-color: #CFDE79;
  display: grid;
  place-items: center;
}

.avatar > img {
  border: 1px solid #c2c2c2;
  width: 68px;
  height: 68px;
  aspect-ratio: 1/1;
  object-fit: cover;
  border-radius: 50%;
  background-color: #FFF;
}

  .outside-left, .outside-right {
    position: absolute;
    &::before, &::after  {
        width: 13px !important;
        background-color: #844AFF !important;
    }
  }
  .outside-left {
    left: -8px !important;
  }
  .outside-right {
    right: -8px !important;
  }
`;

export const WrapperButton = styled.div`
    display: flex;
    max-width: 315px;
    height: 80px;
    align-items: flex-end;
    justify-content: center;
    padding-top: 20px;
`;

export const WrapperText = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;
export const WrapperForm = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    max-width: 315px;
