    height: 45px;
    border:1px solid red;
    justify-content: space-around;

`;

export const WrapperLoader = styled.div`
    display: flex;
    place-items: center;
    place-content: center;
    width: 315px;
    height: 55px;
`;
export const WrapperValidation = styled.div`
    display: flex;
    flex-direction: column;
     .validation {
        font-size: 12px;
        color: #FD5526;
        margin: -25px 0 5px;
     }
`;
export const Container = styled.div`
    display: flex;
    width:100%;
    max-height: 40px;
    min-height: 40px;
    border-radius:5px;
    margin:3px 2%;
    justify-content:center;
    }


`;

export const Input = styled.input`
display: flex;
max-height: 32px;
min-height: 32px;
min-width: 600px;

border:1px solid #000;
border-radius:5px;
margin:3px 2%;
padding: 0 9px;
}
`;
