/* eslint-disable no-param-reassign */
/* eslint-disable no-unused-vars */
import React, { InputHTMLAttributes, ChangeEvent } from 'react';
//import formatCP from '../../utils/formatCPF';
//import phoneMaskHelper from '../../utils/phoneMaskHelper';
import Label from '../Label';
import * as S from './styles';

type InputProps = InputHTMLAttributes<HTMLInputElement> & {
    onChange?: (event: ChangeEvent<HTMLInputElement>) => void
    label?: string;
    placeholder?: string;
    name: string;
    type: string;
    suffix?: React.ReactNode;
    preffix?: React.ReactNode;
    onKeyPress?: (event: any) => void;
    required?: boolean;
    error?: boolean
}

const Input: React.FC<InputProps> = ({
  onChange, type, suffix, preffix, placeholder, onKeyPress, name, label, required, error,
}: InputProps) => {
  const handleInput = () => {
    error = false;
  };
  return (
    <S.Container suffixIcon={!!suffix} prefixIcon={!!preffix}>
      {preffix}
      <S.Wrapper>
        {!!label && <Label htmlFor={name} required={required}>{label}</Label>}
        <S.Input
          id={name}
          type={type}
          placeholder={placeholder}
          onChange={onChange}
          onFocus={() => handleInput}
        />
      </S.Wrapper>
      {suffix}
    </S.Container>
  );
};
export default Input;
