import styled from 'styled-components';
import { Space as AntdSpace } from 'antd';

export const CustomSpace = styled(AntdSpace)`
  display: flex;
  justify-content: center;
  float: left;
  height: auto;
  max-width: 700px;
  width: 90vw; // define a largura em relação à largura da janela de visualização
  justify-content: space-around;
`;
