/* eslint-disable import/prefer-default-export */
import styled from 'styled-components';
import { TypographyProps } from './index';

type TypographyStyleProps = Omit<TypographyProps, 'children'>;

export const Typography = styled.div<TypographyStyleProps>`
    background: transparent !important;
    flex-wrap: wrap;

    font-family: ${({ fontFamily }) => (fontFamily ? `${fontFamily}` : 'Montserrat')};
    font-size: ${({ fontSize }) => `${fontSize}px`};

    ${({ strong }) => strong && `
       font-weight: bold !important;
    `}
    ${({ italic }) => italic && `
       font-style: italic !important;
    `}
    ${({ style }) => `${style} !important`}

    ${({ type }) => type === 'default' && `
           background: transparent;
           color: #000000d9;
    `}
    ${({ type }) => type === 'secondary' && `
           background: transparent;
           color: #00000073;
    `}
    ${({ type }) => type === 'success' && `
           background: transparent;
           color: #52c41a;
    `}
    ${({ type }) => type === 'error' && `
           background: transparent;
           color: #ff4d4f;
    `}
    ${({ type }) => type === 'warning' && `
           background: transparent;
           color: #faad14;
    `} 
    ${({ type }) => type === 'common' && `
           background: transparent;
           color: #844AFF;
     `}
    ${({ type }) => type === 'subtitle' && `
           background: transparent;
           color: #EA92C5;
     `}    
`;
