import React, { useEffect, useState } from 'react';
import Service from "../../services/user.service";
import Button from "../../components/Button";
import { Form } from 'antd';
import List from '../../components/Badge/badge';
import Spin from '../../components/Spin';


import * as S from './style';

const Home: React.FC = () => {

  const [loading, setLoading] = useState(false);

  const [session, setSession] = useState<string | null>(null);
  const [texto, setTexto] = useState<any>([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    const sessionLocal = localStorage.getItem('mg@session');
    if (sessionLocal) {
      setSession(JSON.parse(sessionLocal).info);
    } else {
      getSession();
    }
  }, []);
  const getSaudation = () => {
    const curHr = new Date().getHours();
    if (curHr < 12) {
      return 'Bom dia';
    } if (curHr < 18) {
      return 'Boa tarde';
    }
    return 'Boa noite';
  };

  const getSession = async () => {
    try {
      const { time, info }: { time: string, info: string } = await Service.getPublicContent('/busca');
      setSession(info);
      const sessionData = {
        time,
        info,
      };
      //passa para json a session
      localStorage.setItem('mg@session', JSON.stringify(sessionData));
    } catch (error) {
      console.log(error);
    }
  };

  const handleSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    try {
      setLoading(true);
      const { time, }: { time: string, info: string } = await Service.post('/ache', { ...event.currentTarget.elements });
      setTexto(time);
      setLoading(false);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <S.Wrapper>
      <S.WrapperTitle>
        <S.Title>
          {`${getSaudation()},${session}`}{texto}
        </S.Title>
      </S.WrapperTitle>

      <S.WrapperSearch>
        <S.Container>
          <Form
            layout="vertical"
            name="basic"
            initialValues={{ remember: true }}
            onClick={handleSubmit}
          >
            <S.Input
              id='1'
              type='text'
              name='questions'
              placeholder='Diga algo'
              value={message}
              onChange={event => setMessage(event.target.value)}
            />
            <Button
              //onClick={onClick}
              type="submit"
              style={{ borderRadius: '5px' }}
            >
              Send
            </Button>
          </Form>
        </S.Container>
      </S.WrapperSearch>

      <S.WrapperContent>
        {!loading && (
          <List />
        )}
        {loading && (
          <S.WrapperLoader>
            <Spin data-tst="login-loader" />
          </S.WrapperLoader>
        )}

      </S.WrapperContent>
    </S.Wrapper>

  );

}
export default Home;
