/* eslint-disable import/prefer-default-export */
import styled from 'styled-components';

interface InputProps {
  suffixIcon?: boolean
  prefixIcon?: boolean
}

export const Container = styled.div<InputProps>`
    display: flex;
    width:100%;
    max-height: 40px;
    min-height: 40px;
    border-radius:5px;
    margin:3px 2%;
    }


`;

export const Input = styled.input`
display: flex;
max-height: 32px;
min-height: 32px;
min-width: 600px;

border:1px solid #000;
border-radius:5px;
margin:3px 2%;
padding: 0 9px;
}
`;
