/* eslint-disable no-unused-vars */
import React, { useState } from 'react';
import { Form } from 'antd';
import { EyeInvisibleOutlined, EyeOutlined } from '@ant-design/icons';
import Input from '../../components/Input';
import * as S from './style';
import Button from '../../components/Button';
import Spin from '../../components/Spin';

interface AuthenticatinProps {
    loading: boolean;
    onFinish: (value: any) => void;
    requestError: boolean;
    setRequestError: React.Dispatch<React.SetStateAction<boolean>>;
    requestErrorMessage: any;
}

const Login: React.FC<AuthenticatinProps> = ({
  loading, onFinish, requestError, setRequestError, requestErrorMessage,
}: AuthenticatinProps) => {
  const [showPassword, setShowPassword] = useState(false);
  const [hasValue, setHasValue] = useState(false);

  const handleShowPassword = () => {
    setShowPassword((prev) => !prev);
  };

  const validateRequest = async (rule: any, value: any) => {
    setHasValue(!!value);
    if (requestError) {
      setRequestError(false);
      return Promise.reject();
    }
    setRequestError(false);
    return Promise.resolve();
  };

  return (
    <Form
      layout="vertical"
      name="basic"
      initialValues={{ remember: true }}
      onFinish={onFinish}
      data-tst="login-authentication-form"
    >
      <S.WrapperForm>
        <Form.Item
          name="email"
          rules={[
            { required: true, message: 'Por favor, digite um e-mail.' },
            { type: 'email', message: 'Por favor, digite um e-mail válido.' },
          ]}
        >
          <Input
            type="text"
            label="E-mail"
            name="email"
            data-tst="login-email-input"
          />
        </Form.Item>
        <S.WrapperValidation>
          <Form.Item
            name="password"
            rules={[
              { required: true, message: 'Por favor, digite sua senha.' },
              { validator: validateRequest },
            ]}
          >
            <Input
              type={showPassword ? 'text' : 'password'}
              label="Senha"
              name="password"
              suffix={showPassword
                ? <EyeOutlined onClick={handleShowPassword} />
                : <EyeInvisibleOutlined onClick={handleShowPassword} />}
              data-tst="login-password-input"
            />
          </Form.Item>
          {hasValue && requestError && (
            <span className="validation">{requestErrorMessage}</span>
          )}
        </S.WrapperValidation>
      </S.WrapperForm>
      <S.WrapperButton>
        {!loading && (
          <Button
            data-tst="login-authentication-button"
            type="submit"
            width={315}
            height={55}
          >
            CONTINUAR
          </Button>
        )}
        {loading && (
          <S.WrapperLoader>
            <Spin data-tst="login-loader" />
          </S.WrapperLoader>
        )}
      </S.WrapperButton>
    </Form>
  );
};
export default Login;
