/* eslint-disable no-unused-vars */
import React, { useEffect, useState } from 'react';
import {
  FacebookOutlined, FireOutlined, InstagramOutlined, LinkedinOutlined,
} from '@ant-design/icons';
import {
  Divider, Row, Space, Spin,
} from 'antd';
import InfiniteScroll from 'react-infinite-scroll-component';
import * as S from '../style';
import ApiInstanceHotNews from '../../../services/apiHotNews';
import NewsProps from '../../../models/News';

const socialMedias = [
  {
    id: 1,
    icon: <LinkedinOutlined />,
    href: 'https://www.linkedin.com/company/smart-nx/',
  },
  {
    id: 2,
    icon: <InstagramOutlined />,
    href: 'https://www.instagram.com/smartnx_/',
  },
  {
    id: 3,
    icon: <FacebookOutlined />,
    href: 'https://www.facebook.com/curtaSmartNX',
  },
];

interface NewsCardProps {
  load: boolean;
}

const Spinning = () => (
  <S.WrapperSpinning>
    <Space size="middle">
      <Spin />
    </Space>
  </S.WrapperSpinning>
);

const NewsCard = ({ load }: NewsCardProps) => {
  const [allHotNews, setAllHotNews] = useState<NewsProps[]>([]);
  const [historyPage, setHistoryPage] = useState<number>(1);
  const [hasMoreHistoryData, setHasMoreHistoryData] = useState<boolean>(false);

  useEffect(() => {
    if (load) {
      getHotNews();
    } else {
      setAllHotNews([]);
    }
  }, [load]);

  const getHotNews = async () => {
    try {
      const data: NewsProps[] = await ApiInstanceHotNews.get(`/hot-news/news?limit=5&page=${historyPage}`);
      if (data.length < 5) {
        setHasMoreHistoryData(false);
      }
      if (data.length >= 5) {
        setHasMoreHistoryData(true);
        setHistoryPage((prev) => prev + 1);
      }
      setAllHotNews((prev) => [...prev, ...data]);
    } catch (err) {
      console.log(err);
    }
  };

  return (
    <S.Card style={{ minHeight: '310px', display: 'block' }}>
      <S.CardHeader>
        <div className="card-icon"><FireOutlined /></div>
        <S.CardWrapper>
          <S.CardTitle>
            Novidades Quentes
          </S.CardTitle>
          <S.CardDescription>
            Tudo sobre o Suite e o mercado omnichannel para você.
          </S.CardDescription>
        </S.CardWrapper>
        <S.SocialMedia>
          {socialMedias?.map((item) => (
            <S.SocialMediaItem key={item.id} onClick={() => { window.open(item.href, '_blank'); }}>
              {item.icon}
            </S.SocialMediaItem>
          ))}
        </S.SocialMedia>
      </S.CardHeader>
      <S.CardBody>
        <Row
          id="scrollableDiv"
          className="scrollableDiv"
        >
          <InfiniteScroll
            dataLength={allHotNews.length}
            next={getHotNews}
            hasMore={hasMoreHistoryData}
            loader={<Spinning />}
            endMessage={<Divider plain>Não há mais registros.</Divider>}
            scrollableTarget="scrollableDiv"
          >
            {allHotNews?.map((item) => (
              <S.CardBodyItem key={item.id} onClick={() => { window.open(item.url_news, '_blank'); }}>
                <S.CardBodyItemImg src={item.url_image} />
                <S.CardBodyItemWrapper>
                  <S.CardBodyItemTitle>
                    {item.title}
                  </S.CardBodyItemTitle>
                  <S.CardBodyItemDescription>
                    {item.description}
                  </S.CardBodyItemDescription>
                </S.CardBodyItemWrapper>
              </S.CardBodyItem>
            ))}
          </InfiniteScroll>
        </Row>
      </S.CardBody>
    </S.Card>
  );
};

export default NewsCard;
