import React, {useEffect, useState } from 'react';
import UserService from "../../services/user.service";
import * as S from './style';

const Home: React.FC = () => {

  const [version, setVersion] = useState<any>();

  const getVersion = async () => {
    try {
      const { fun, info }: {fun:string, info: string} = await UserService.getPublicContent('/busca');
      setVersion(info);
      const versionData = {
        fun,
        info,
      };
      localStorage.setItem('suite@version', JSON.stringify(versionData));
      console.log(localStorage)
    } catch (error) {
      console.log('error', error);
    }
  };

  const getSaudation = () => {
    const curHr = new Date().getHours();
    if (curHr < 12) {
      return 'Bom dia';
    } if (curHr < 18) {
      return 'Boa tarde';
    }
    return 'Boa noite';
  };
  useEffect(() => {
    //const versionLocal = await UserService.getPublicContent();
    const versionLocal = localStorage.getItem('suite@version');

    if (versionLocal) {
      setVersion(JSON.parse(versionLocal).info);
    } else {
      getVersion();
    }
    ;
  }, []);


    return (
      <div className="card-home">
        {/* {this.state.users.map(user => ( */}
          <div className="card-home-list" key=''>
          <S.Title>
          {`${getSaudation()}`}
            {`Versão ${version || ''}`}
          </S.Title>
          </div>
        {/* ))} */}
      </div>
    );

}
export default Home;
