import React, { useEffect, useState } from 'react';
import Service from "../../services/user.service";
import { RocketOutlined, } from '@ant-design/icons/lib/icons';
import Button from "../../components/Button";
import NewsCard from './NewsCard';
import Slide from '../../components/Slide/carrousel';
import List from '../../components/Badge/badge';

import * as S from './style';

const homeCards = [
  {
    id: 1,
    icon: <RocketOutlined />,
    title: 'O Suite é novo para você?',
    description: 'Tire dúvidas e aprenda a utilizá-lo para aproveitar o seu máximo.',
    hrefOut: 'https://sac.smartnx.com/kb',
  },
  {
    id: 2,
    icon: <RocketOutlined />,
    title: 'Monitorar atendimento',
    description: 'Monitore e auxilie seus operadores em tempo real.',
    hrefIn: '/chat',
  },
];

const Home: React.FC = () => {

  const [session, setSession] = useState<string | null>(null);
  const [modalManageHome, setModalManageHome] = useState<boolean>(false);

  useEffect(() => {
    //localStorage.removeItem('mg@session');
    //localStorage.clear();
    //pega a sessiona que esta em json .time
    const sessionLocal = localStorage.getItem('mg@session');
    if (sessionLocal) {
      setSession(JSON.parse(sessionLocal).info);
    } else {
      getSession();
    }
  }, []);
  const getSaudation = () => {
    const curHr = new Date().getHours();
    if (curHr < 12) {
      return 'Bom dia';
    } if (curHr < 18) {
      return 'Boa tarde';
    }
    return 'Boa noite';
  };
  const getSession = async () => {
    try {
      const { time, info }: { time: string, info: string } = await Service.getUserData('/busca');
      setSession(info);
      const sessionData = {
        time,
        info,
      };
      //passa para json a session
      localStorage.setItem('mg@session', JSON.stringify(sessionData));
    } catch (error) {
      console.log(error);
    }
  };
  // const getData = async () => {
  //   try {
  //     const { info, info }: { time: string, info: string } = await Service.getPublicContent('/busca');
  //     const sessionData = {
  //       time,
  //       info,
  //     };
  //     setSession(info);

  //     //passa para json a session
  //   } catch (error) {
  //     console.log(error);
  //   }
  // };

  return (
    <S.Wrapper>
      <S.WrapperTitle>
        <S.Title>
          {`${getSaudation()},${session}`}
        </S.Title>
        <Button
          onClick={() => { setModalManageHome(true); }}
          type="button"
          style={{ borderRadius: '5px' }}
        >
          Gerenciar home
        </Button>
      </S.WrapperTitle>

      <S.WrapperContent>
        <S.GroupCards>
          {homeCards.map((card) => (
            <S.Card
              key={card.id}
              clickable={!!(card.hrefIn || card.hrefOut)}
            //onClick={() => cardClick(card)}
            >
              <S.CardHeader>
                <div className="card-icon">{card.icon}</div>
                <S.CardWrapper>
                  <S.CardTitle>
                    {card.title}
                  </S.CardTitle>
                  <S.CardDescription>
                    {card.description}
                  </S.CardDescription>
                </S.CardWrapper>
              </S.CardHeader>
            </S.Card>
          ))}
          <NewsCard load={!modalManageHome} />
        </S.GroupCards>
        <S.GroupLists>
          <List />
        </S.GroupLists>
      </S.WrapperContent>
    </S.Wrapper>
    // <div className="card-home">
    //   {/* {this.state.users.map(user => ( */}
    //   <div className="card-home-list" key=''>
    //     <S.Title>
    //       {`${getSaudation()}`}
    //       {`Versão ${session || ''}`}
    //     </S.Title>
    //   </div>
    //   {/* ))} */}
    // </div>
  );

}
export default Home;
