import React, {useEffect, useState } from 'react';
import UserService from "../../services/user.service";
import * as S from './style';

const Home: React.FC = () => {

  const [version, setVersion] = useState<string | null>(null);

  useEffect(() => {
  const versionLocal = localStorage.getItem('suite@version');
      if (versionLocal) {
        setVersion(JSON.parse(versionLocal).info);
      } else {
        getLatestVersion();
      }
    }, []);
  const getSaudation = () => {
    const curHr = new Date().getHours();
    if (curHr < 12) {
      return 'Bom dia';
    } if (curHr < 18) {
      return 'Boa tarde';
    }
    return 'Boa noite';
  };
  const getLatestVersion = async () => {
    try {
      const { id, info }: {id:number, info: string} = await UserService.getPublicContent('busca');
      setVersion(info);
      const versionData = {
        id,
        info,
      };
      localStorage.setItem('suite@version', JSON.stringify(versionData));
    } catch (error) {
      console.log(error);
    }
  };


    return (
      <div className="card-home">
        {/* {this.state.users.map(user => ( */}
          <div className="card-home-list" key=''>
          <S.Title>
          {`${getSaudation()}`}
            {`Versão ${version || '1.0'}`}
          </S.Title>
          </div>
        {/* ))} */}
      </div>
    );

}
export default Home;
