/* eslint-disable no-unused-vars */
import React, { useState } from 'react';
import { Form } from 'antd';
import { EyeInvisibleOutlined, EyeOutlined } from '@ant-design/icons';
import Button from '../../../components/Button';
import Input from '../../../components/Input';
import Spin from '../../../components/Spin';
import * as S from './style';

interface AuthenticatinProps {
  loading: boolean;

}
const Authentication: React.FC<AuthenticatinProps> = ({
  loading,
}: AuthenticatinProps) => {  const [showPassword, setShowPassword] = useState(false);

  const handleShowPassword = () => {
    setShowPassword((prev) => !prev);
  };

  const validateRequest = async (rule: any, value: any) => {

  };

  return (
      <Form
        layout="vertical"
        name="basic"
        initialValues={{ remember: true }}
        data-tst="login-authentication-form"
      >
      <S.WrapperForm>
        <Form.Item
          name="email"
          rules={[
            { required: true, message: 'Por favor, digite um e-mail.' },
            { type: 'email', message: 'Por favor, digite um e-mail válido.' },
          ]}
        >
            <Input
              type="text"
              label="E-mail"
              name="email"
              data-tst="login-email-input"
            />
        </Form.Item>
        <S.WrapperValidation>
          <Form.Item
            name="password"
            rules={[
              { required: true, message: 'Por favor, digite sua senha.' },
              { validator: validateRequest },
            ]}
            >
            <Input
              type={showPassword ? 'text' : 'password'}
              label="Senha"
              name="password"
              suffix={showPassword
                ? <EyeOutlined onClick={handleShowPassword} />
                : <EyeInvisibleOutlined onClick={handleShowPassword} />}
              data-tst="login-password-input"
            />
          </Form.Item>

        </S.WrapperValidation>
      </S.WrapperForm>
      <S.WrapperButton>
      {!loading && (
          <Button
            data-tst="login-authentication-button"
            type="submit"
            width={315}
            height={55}
          >
            CONTINUAR
          </Button>
        )}
        {loading && (
          <S.WrapperLoader>
            <Spin data-tst="login-loader" />
          </S.WrapperLoader>
        )}
        <S.WrapperLoader>
          <Spin data-tst="login-loader" />
        </S.WrapperLoader>
      </S.WrapperButton>
    </Form>
  );
};
export default Authentication;
