import React, { useEffect, useState } from 'react';
import Service from "../../services/user.service";
import Input from '../../components/Input';
import List from '../../components/Badge/badge';


import * as S from './style';



const Home: React.FC = () => {

  const [session, setSession] = useState<string | null>(null);
  const [texto, setTexto] = useState<any>([]);
  useEffect(() => {
    //localStorage.removeItem('mg@session');
    //localStorage.clear();
    //pega a sessiona que esta em json .time
    const sessionLocal = localStorage.getItem('mg@session');
    if (sessionLocal) {
      setSession(JSON.parse(sessionLocal).info);
    } else {
      getSession();
    }
  }, []);
  const getSaudation = () => {
    const curHr = new Date().getHours();
    if (curHr < 12) {
      return 'Bom dia';
    } if (curHr < 18) {
      return 'Boa tarde';
    }
    return 'Boa noite';
  };
  const getSession = async () => {
    try {
      const { time, info }: { time: string, info: string } = await Service.getUserData('/busca');
      setSession(info);
      const sessionData = {
        time,
        info,
      };
      //passa para json a session
      localStorage.setItem('mg@session', JSON.stringify(sessionData));
    } catch (error) {
      console.log(error);
    }
  };
  const getData = async () => {
    try {
      const { time, }: { time: string, info: string } = await Service.getPublicContent('/busca');

      setTexto(time);

      //passa para json a session
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <S.Wrapper>
      <S.WrapperTitle>
        <S.Title>
          {`${getSaudation()},${session}`}${texto}
        </S.Title>
      </S.WrapperTitle>

      <S.WrapperSearch>
        <Input
          type="text"
          label="E-mail"
          name="email"
          placeholder='Diga algo'
          onClick={() => {getData()}}
        />
      </S.WrapperSearch>

      <S.WrapperContent>
          <List />
      </S.WrapperContent>
    </S.Wrapper>

  );

}
export default Home;
