import React, { useEffect, useState } from 'react';
import Service from "../../services/user.service";
import Input from '../../components/Input';
import List from '../../components/Badge/badge';
import Spin from '../../components/Spin';


import * as S from './style';

const Home: React.FC = () => {
  const [loading, setLoading] = useState(false);

  const [session, setSession] = useState<string | null>(null);
  const [texto, setTexto] = useState<any>([]);

  useEffect(() => {
    const sessionLocal = localStorage.getItem('mg@session');
    if (sessionLocal) {
      setSession(JSON.parse(sessionLocal).info);
    } else {
      getSession();
    }
  }, []);
  const getSaudation = () => {
    const curHr = new Date().getHours();
    if (curHr < 12) {
      return 'Bom dia';
    } if (curHr < 18) {
      return 'Boa tarde';
    }
    return 'Boa noite';
  };

  const getSession = async () => {
    try {
      const { time, info }: { time: string, info: string } = await Service.getUserData('/busca');
      setSession(info);
      const sessionData = {
        time,
        info,
      };
      //passa para json a session
      localStorage.setItem('mg@session', JSON.stringify(sessionData));
    } catch (error) {
      console.log(error);
    }
  };
  const getData = async () => {

    try {
      setLoading(true);
      const { time, }: { time: string, info: string } = await Service.getPublicContent('/busca');
      setTexto(time);
      setLoading(false);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <S.Wrapper>
      <S.WrapperTitle>
        <S.Title>
          {`${getSaudation()},${session}`}{texto}
        </S.Title>
      </S.WrapperTitle>

      <S.WrapperSearch>
        <Input
          type="text"
          name="question"
          placeholder='Diga algo'
          onClick={() => { getData() }}
        />
      </S.WrapperSearch>

      <S.WrapperContent>
        {!loading && (
          <List />
        )}
        {loading && (
          <S.WrapperLoader>
            <Spin data-tst="login-loader" />
          </S.WrapperLoader>
        )}

      </S.WrapperContent>
    </S.Wrapper>

  );

}
export default Home;
