/* eslint-disable import/prefer-default-export */
import styled from 'styled-components';

interface InputProps {
  suffixIcon?: boolean
  prefixIcon?: boolean
}

export const Container = styled.div<InputProps>`
    display: flex;
    width:100%;
    max-height: 40px;
    min-height: 40px;
    border-radius:5px;
    margin:3px 2%;
    justify-content:center;
    }


`;
export const Wrapper = styled.div`
    display: flex;
    flex-direction: column;
    color: #c2c2c2;
    max-width: 100%;

    &:focus-within {
      label {
        transition: font-size 0.2s;
        transition: margin-top 0.2s;
        color: #8449FF;
        font-weight: 500;
        font-size: 12px;
        margin-top: -4px;
      }

      input {
        transition: margin-top 0.2s;
        border-bottom: 1px solid #8449FF;
        margin-top: 4px;
      }
    }
`;

export const Input = styled.input`
display: flex;
max-height: 32px;
min-height: 32px;
min-width: 600px;

border:1px solid #000;
border-radius:5px;
margin:3px 2%;
padding: 0 9px;
}
`;
