/* eslint-disable indent */
/* eslint-disable import/prefer-default-export */
import styled from 'styled-components';

export const Wrapper = styled.div`
    padding-bottom: 30px;

    .modal-centered {
        display: flex !important;
        align-items: center;
        justify-content: center;
    }
`;

export const WrapperSpinning = styled.div`
  display: flex;
  place-items: center;
  place-content: center;
  margin: 0 5px;  
`;

export const WrapperTitle = styled.div`
    display: flex;
    align-items: center;
`;
export const WrapperSearch = styled.div`
    display: flex;
        text-align: center;

    align-items: center;
`;
export const Title = styled.h2`
    width: 90%;
    padding: 30px 0;
    font-size: 24px;
    color: #666666;
    font-weight: 600;
    text-align: center;
`;

export const WrapperContent = styled.div`
    display: flex;
    grid-template-columns: repeat(2, 1fr);
    gap: 40px;
    @media (max-width: 1140px) {
        grid-template-columns: 1fr;
    }
`;

type statusChannelProps = {
    able: boolean
}

export const WrapperIconChat = styled.div<statusChannelProps>`
    display: flex;
    width: 100%;
    height: 100%;
    position: relative;
    left: 50%;
    transform: translateX(-50%);
    place-items: center;
    place-content: center;
    padding: 10px;
    border-radius: 100px;
    margin-top: 5px;
    ${({ able }) => (able ? `
    background: #e74c3c;
    `
        : `background: #7c7f80;
    color:#000 !important;`)}
    ;
    svg {
        padding: 5px;
        font-size: 60px;
        color: #fff;
    }
`;


export const GroupLists = styled.div`
    display: grid;
    grid-template-columns: 1fr;
    gap: 30px;
`;

export const WrapperAttendance = styled.div`
    width: 100%;
    display: flex;
    gap: 30px;
    justify-content: space-between;
`;
export const WrapperValidation = styled.div`
    display: flex;
    flex-direction: column;
     .validation {
        font-size: 12px;
        color: #FD5526;
        margin: -25px 0 5px;
     }
`;
