/* eslint-disable indent */
/* eslint-disable import/prefer-default-export */
import styled from 'styled-components';

export const Wrapper = styled.div`
    padding-bottom: 30px;

    .modal-centered {
        display: flex !important;
        align-items: center;
        justify-content: center;
    }
`;

export const WrapperSpinning = styled.div`
  display: flex;
  place-items: center;
  place-content: center;
  margin: 0 5px;  
`;

export const WrapperTitle = styled.div`
    display: flex;
    align-items: center;
`;

export const Title = styled.h2`
    width: 90%;
    padding: 30px 0;
    font-size: 24px;
    color: #666666;
    font-weight: 600;
`;

export const WrapperContent = styled.div`
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 40px;
    @media (max-width: 1140px) {
        grid-template-columns: 1fr;
    }
`;

type statusChannelProps = {
    able: boolean
}

export const WrapperIconChat = styled.div<statusChannelProps>`
    display: flex;
    width: 100%;
    height: 100%;
    position: relative;
    left: 50%;
    transform: translateX(-50%);
    place-items: center;
    place-content: center;
    padding: 10px;
    border-radius: 100px;
    margin-top: 5px;
    ${({ able }) => (able ? `
    background: #e74c3c;
    `
        : `background: #7c7f80;
    color:#000 !important;`)}
    ;
    svg {
        padding: 5px;
        font-size: 60px;
        color: #fff;
    }
`;

export const GroupCards = styled.div`
    display: grid;
    grid-template-columns: 1fr;
    gap: 30px; 
`;

interface CardProps {
    clickable?: boolean;
}

export const Card = styled.div<CardProps>`
    display: flex;
    align-items: center;
    padding: 20px 30px;
    min-width: 450px;
    background: #FFFFFF;
    box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.15);
    border-radius: 6px;
    cursor: ${({ clickable }) => (clickable ? 'pointer' : 'default')};
`;

export const CardWrapper = styled.div`
`;

export const CardHeader = styled.div`
    display: flex;
    align-items: center;
    gap: 30px;
    .card-icon {
        margin-top: -20px;
        font-size: 1.9rem;
        width: 1.9rem;
        height: 1.9rem;
        color: #844AFF;
    }
`;

export const CardTitle = styled.div`
    font-size: 1.2rem;
    color: #844AFF;
    font-weight: 500;
    line-height: 1.2;
`;

export const CardDescription = styled.div`
    font-size: .9rem;
    color: #666666;
    font-weight: 500;
`;

export const CardHeaderIcon = styled.div`
    display: flex;
    align-items: center;
    gap: 10px;
`;

export const SocialMedia = styled.div`
    display: flex;
    align-items: center;
    gap: 10px;
    align-self: flex-start;
    margin-left: auto; 
`;

export const SocialMediaItem = styled.div`
    cursor: pointer;
    svg {
        font-size: 1.2rem;
        color: #666666;
        fill: #666666 !important;
    }   
`;

export const CardBody = styled.div`
    overflow: auto !important;
    .infinite-scroll-component__outerdiv{
        width: 100% !important;
    }
    .scrollableDiv {
    height: 210px;
    margin-top: 30px;
    margin-left: 10px;
    gap: 30px;
    overflow-y: scroll;

    ::-webkit-scrollbar {
        width: 8px;
    }

    /* Track */
    ::-webkit-scrollbar-track {
        background: #f1f1f1; 
        border: 1px;
        border-radius: 30px;
    }

    /* Handle */
    ::-webkit-scrollbar-thumb {
        background: #999999;
        border-radius: 30px;
    }

    /* Handle on hover */
    ::-webkit-scrollbar-thumb:hover {
        background: #F1F1F1; 
    }
    }
`;

export const CardBodyItem = styled.div`
    margin-bottom: 30px;
    display: flex;
    align-items: top;
    gap: 20px;
    cursor: pointer;
`;

interface CardBodyItemImgProps {
    src: string;
}

export const CardBodyItemImg = styled.img<CardBodyItemImgProps>`
    background-image: url(${({ src }) => src});
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
    width: 110px;
    height: 110px;
    min-width: 110px;
    min-height: 110px;
`;

export const CardBodyItemWrapper = styled.div`
    display: -webkit-box;
    max-width: 400px;
    max-height: 100px;
    -webkit-line-clamp: 4;
    -webkit-box-orient: vertical;
    overflow: hidden;
`;

export const CardBodyItemTitle = styled.div`
    font-size: 1.1rem;
    font-weight: 600;
`;

export const CardBodyItemDescription = styled.div`
    font-size: .9rem;
    color: #5C5B5B;
    font-weight: 500;
`;

export const GroupCharts = styled.div`
    display: grid;
    grid-template-columns: 1fr;
    gap: 30px;
`;

export const WrapperAttendance = styled.div`
    width: 100%;
    display: flex;
    gap: 30px;
    justify-content: space-between;
`;
