/* eslint-disable import/prefer-default-export */
import styled, { css } from 'styled-components';

export const WrapperInput = styled.div`
  padding: 0 32px;
  font-size: 14px !important;
`;

export const WrapperCarouselList = styled.div`
`;

export const WrapperFliker = styled.div`
  padding: 36px;
  position: relative;
  width: 100%;

  .card {
    width: 100%;
    display: flex;
    flex-direction: column;
    align-items: center;
  }

  .avatar {
  width: 68px;
  height: 68px;
  border-radius: 50%;
  background-color: #CFDE79;
  display: grid;
  place-items: center;
}

.avatar > img {
  border: 1px solid #c2c2c2;
  width: 68px;
  height: 68px;
  aspect-ratio: 1/1;
  object-fit: cover;
  border-radius: 50%;
  background-color: #FFF;
}

  .outside-left, .outside-right {
    position: absolute;
    &::before, &::after  {
        width: 13px !important;
        background-color: #844AFF !important;
    }
  }
  .outside-left {
    left: -8px !important;
  }
  .outside-right {
    right: -8px !important;
  }
`;

interface WrapperButtonProps {
  isDisable: boolean;
}

export const WrapperButton = styled.div<WrapperButtonProps>`
    display: flex;
    max-width: 315px;
    height: 80px;
    align-items: flex-end;
    justify-content: center;
    padding-top: 20px;

    ${({ isDisable }) => !isDisable && css`
    button {
      border: 1px solid #844AFF !important; 
      color: #844AFF !important;

      &:hover {
        color: #FFF !important;
      }
    }
    `};
`;

export const WrapperText = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;
export const WrapperForm = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    max-width: 315px;
    height: 200px;
    justify-content: space-around;
    input {
        width: 260px;
    }
`;

export const WrapperLoader = styled.div`
    display: flex;
    place-items: center;
    place-content: center;
    width: 315px;
    height: 55px;
`;
export const WrapperValidation = styled.div`
    display: flex;
    flex-direction: column;
     .validation {
        font-size: 12px;
        color: #FD5526;
        margin: -25px 0 5px;
     }
`;
export const Container = styled.div`
    display: flex;
    width:100%;
    max-height: 40px;
    min-height: 40px;
    border-radius:5px;
    margin:3px 2%;
    justify-content:center;
    }


`;

export const Input = styled.input`
display: flex;
max-height: 32px;
min-height: 32px;
min-width: 600px;

border:1px solid #000;
border-radius:5px;
margin:3px 2%;
padding: 0 9px;
}
`;
