/* eslint-disable no-unused-vars */
import React from 'react';
import { Form } from 'antd';
import Input from '../../../components/Input';
import * as S from './style';
import Button from '../../../components/Button';
import { useRef } from 'react';

interface AuthenticatinProps {
  onFinish: (value: any) => void;

}

const Search: React.FC<AuthenticatinProps> = ({
  onFinish,
}: AuthenticatinProps) => {

  const handleFormSubmit = () => {
    formRef.current?.submit();
  };
  const formRef = useRef(null);


  return (


    <Form
      layout="vertical"
      name="basic"
      initialValues={{ remember: true }}
      onFinish={onFinish}
      ref={formRef} // adiciona a referência ao formulário

    >
      <S.WrapperForm>
        <Form.Item
          name="question"
          rules={[
            { required: true, message: 'Por favor, digite algo.' },
          ]}
        >
          <Input
            type="text"
            name="question"
          />
        </Form.Item>


        <Button
          type="button"
          width={120}
          height={32}
          onClick={handleFormSubmit} // adiciona o evento onClick ao botão

        >
          Send
        </Button>
        </S.WrapperForm>

    </Form>


  );
};
export default Search;
