import React, { useEffect, useState } from 'react';
import Service from "../../services/service";
import List from '../../components/Badge/badge';
import Spin from '../../components/Spin';
import Search from './wrapper/search';

import * as S from './style';

const Home: React.FC = () => {

  const [loading, setLoading] = useState(false);

  const [saudation, setSaudation] = useState<string | string>('Bem Vindo!');
  const [responses, setResposnses] = useState<any>([]);

  useEffect(() => {
    getSaudation()
  }, []);

  const getSaudation = async () => {
   try {
      setLoading(true);
      const { saudation, }: { saudation: string } = await Service.get('/saudation');
      setSaudation(saudation);
      setLoading(false);
    } catch (error) {
      console.log(error);
    }
  };

  const getData = async (values: any)  => {
    try {
      setLoading(true);
      const { fun, response, }: { fun: string, response: string } = await Service.post('/search',{ ...values });
      const responses = {
        fun,
        response
      };
      setResposnses(responses);
      setLoading(false);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <S.Wrapper>
      <S.WrapperTitle>
        <S.Title>
          {saudation}
        </S.Title>
      </S.WrapperTitle>

      <S.WrapperSearch>
        <Search
          onFinish={getData}
        />
      </S.WrapperSearch>
      <S.WrapperTitle>
        <S.Title>        
        {responses.fun}{responses.response}
        </S.Title>
      </S.WrapperTitle>
      <S.WrapperContent>
        {!loading && (
          <List />
        )}
        {loading && (
          <S.WrapperLoader>
            <Spin data-tst="login-loader" />
          </S.WrapperLoader>
        )}

      </S.WrapperContent>
    </S.Wrapper>

  );

}
export default Home;
