/* eslint-disable import/prefer-default-export */
import styled from 'styled-components';

interface InputProps {
    suffixIcon?: boolean
    prefixIcon?: boolean
  }

export const Container = styled.div<InputProps>`
    display: flex;
    flex-direction: column;
    max-height: 40px;
    min-height: 40px;
    input {
      ${({ suffixIcon }) => !!suffixIcon && 'padding-right: 20px'}
      ${({ prefixIcon }) => !!prefixIcon && 'padding-left: 20px'}
    }

    svg {
      color: #c2c2c2;
      ${({ suffixIcon }) => !!suffixIcon && `
          position: absolute;
          right: 0;
          top: 20px;
          width: 15px;
          height: 15px;
      `}
       ${({ prefixIcon }) => !!prefixIcon && `
          position: absolute;
          left: 0;
          top: 10px;
          width: 15px;
          height: 15px;
       `}
    }

    input:-webkit-autofill {
      &, &:hover, &:focus, &:active {
        -webkit-box-shadow: 0 0 0px 1000px white inset !important;
      }
    }   
`;

export const Wrapper = styled.div`
    display: flex;
    flex-direction: column;
    color: #c2c2c2;
    max-width: 100%;

    &:focus-within {
      label {
        transition: font-size 0.2s;
        transition: margin-top 0.2s;
        color: #8449FF;
        font-weight: 500;
        font-size: 12px;
        margin-top: -4px;
      }

      input {
        transition: margin-top 0.2s;
        border-bottom: 1px solid #8449FF;
        margin-top: 4px;
      }
    }
`;

export const Input = styled.input`
    display: flex;
    color: #000;
    outline: none;
    transition: filter 0.2s;
    font-weight: 400;
    line-height: 15px;
    font-size: 14px;
    margin: 5px;
    border: none;
    border-bottom: 1px solid #c2c2c2;
    margin: 0;
}
`;
