/* eslint-disable import/prefer-default-export */
import styled from 'styled-components';


export const Input = styled.input`
    display: flex;
    color: #000;
    outline: none;
    transition: filter 0.2s;
    font-weight: 400;
    line-height: 15px;
    font-size: 14px;
    margin: 5px;
    border: none;
    border-bottom: 1px solid #c2c2c2;
    margin: 0;
}
`;
