/* eslint-disable import/prefer-default-export */
import styled, { css } from 'styled-components';

interface LinkProps {
    suffixIcon?: boolean
    prefixIcon?: boolean
    color?: string
  }

const showSuffix = {
  suffixIcon: () => css`
      svg {
        width: 15px;
        height: 15px;
        margin-left: 3px;
      }
    `,
};

const showPrefix = {
  prefixIcon: () => css`
      svg {
        width: 15px;
        height: 15px;
        margin-right: 3px;
      }
    `,
};

export const Link = styled.a<LinkProps>`
    display: flex;
    color: ${({ color }) => (!color ? '#8449FF' : `${color}`)};
    cursor: pointer;
    outline: none;
    transition: filter 0.2s;
    font-weight: 700;
    font-size: 12px;
    margin: 5px;
    ${({ suffixIcon }) => !!suffixIcon && showSuffix.suffixIcon}
    ${({ prefixIcon }) => !!prefixIcon && showPrefix.prefixIcon}

    &:hover {
        filter: brightness(0.8);
        color: ${({ color }) => (!color ? '#8449FF' : `${color}`)};
    }
`;
