import axios from 'axios';
import authHeader from './auth-header';

const { REACT_APP_API_VERSION } = process.env;

const BASE_URL = REACT_APP_API_VERSION || 'http://localhost:3000';

class UserService {
  getPublicContent<a>(url: string): Promise<a> {
    return new Promise(async (resolve, reject) => {
      try {
        //const headers = this.headers({});
        const res = await fetch(`${BASE_URL}${url}`, {
          method: 'GET',
         // headers,
        });
        //const res = await fetch(`${API_URL}${url}`);
        const resJson = await res.json();
        resolve(resJson);

      } catch (error) {
        console.log(error);
        // const errorJson = new Error('Erro ao enviar informaÃ§Ãµes ðŸ˜ž');
        // reject(errorJson);
      }
    });
  }

  async getPublicContent2() {
    return await fetch(BASE_URL + 'frase')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        return data;
      })
      .catch(error => {
        console.error('Error:', error);
      });
  }

  getUserBoard() {
    return axios.get(BASE_URL + 'user', { headers: authHeader() });
  }

  getModeratorBoard() {
    return axios.get(BASE_URL + 'mod', { headers: authHeader() });
  }

  getAdminBoard() {
    return axios.get(BASE_URL + 'admin', { headers: authHeader() });
  }
  get<a>(url: string): Promise<a> {
    return new Promise(async (resolve, reject) => {
      try {
        const headers = this.headers({});
        const res = await fetch(`${BASE_URL}${url}`, {
          method: 'GET',
          headers,
        });
        if (res.status === 204) resolve([] as any);
        const resJson = await res.json();
        if (
          resJson.error
          || res.status.toString()[0] === '4'
          || res.status.toString()[0] === '5'
        ) {
          const error = new Error(resJson.error?.display_message);
          reject(error);
        }
        resolve(resJson);
      } catch (error) {
        console.log(error);
        const errorJson = new Error('Erro ao enviar informaÃ§Ãµes ðŸ˜ž');
        reject(errorJson);
      }
    });
  }

  delete<a>(url: string): Promise<a> {
    return new Promise(async (resolve, reject) => {
      try {
        const headers = this.headers({});
        const res = await fetch(`${BASE_URL}${url}`, {
          method: 'DELETE',
          headers,
        });
        const resJson = await res.json();
        if (
          resJson.error
          || res.status.toString()[0] === '4'
          || res.status.toString()[0] === '5'
        ) {
          const error = new Error(resJson.error.display_message);
          reject(error);
        }
        resolve(resJson);
      } catch (error) {
        console.log(error);
        const errorJson = new Error('Erro ao enviar informaÃ§Ãµes ðŸ˜ž');
        reject(errorJson);
      }
    });
  }

  put<a>(url: string, params: Object): Promise<a> {
    return new Promise(async (resolve, reject) => {
      try {
        const headers = this.headers({});
        const res = await fetch(`${BASE_URL}${url}`, {
          method: 'PUT',
          headers,
          body: JSON.stringify(params),
        });
        const resJson = await res.json();
        if (
          resJson.error
          || res.status.toString()[0] === '4'
          || res.status.toString()[0] === '5'
        ) {
          throw new Error(resJson.error?.display_message);
        }
        resolve(resJson);
      } catch (error) {
        console.log('erro original', error);
        const errorJson = new Error('Erro ao enviar informaÃ§Ãµes ðŸ˜ž');
        reject(errorJson);
      }
    });
  }

  post<a>(url: string, params: Object): Promise<a> {
    return new Promise(async (resolve, reject) => {
      try {
        const headers = this.headers({});
        const res = await fetch(`${BASE_URL}${url}`, {
          method: 'POST',
          headers,
          body: JSON.stringify(params),
        });
        const resJson = await res.json();
        if (
          resJson.error
          || res.status.toString()[0] === '4'
          || res.status.toString()[0] === '5'
        ) {
          const error = new Error(resJson.error?.display_message);
          reject(error);
        }
        resolve(resJson);
      } catch (error: any) {
        console.log(error.message);
        const errorJson = new Error('Erro ao enviar informaÃ§Ãµes ðŸ˜ž');
        reject(errorJson);
      }
    });
  }

  headers(h: Object): any {
    const jwt = window.localStorage.getItem('suite@token');
    const headers = {
      'Content-Type': 'application/json',
      Accept: 'application/json',
      Authorization: `Bearer ${jwt}`,
      ...h,
    };
    return headers;
  }

  getToken(): string | null {
    return window.localStorage.getItem('suite@token');
  }


}
const Service = new UserService();

export default Service;
