import axios from 'axios';
import authHeader from './auth-header';

const API_URL = 'http://localhost:3000';

class UserService {
  getPublicContent<a>(url: string): Promise<a> {
    return new Promise(async (resolve, reject) => {
      try {

        const res = await fetch(`${API_URL}${url}`, {
          method: 'GET',

        });
        const resJson = await res.json();
        resolve(resJson);

      } catch (error) {
        console.log(error);
        // const errorJson = new Error('Erro ao enviar informaÃ§Ãµes ðŸ˜ž');
        // reject(errorJson);
      }
    });
  }

  async getPublicContent2() {
    // const res = await fetch(`${API_URL}${url}`, {
    //   method: 'GET',
    //   headers,
    // });
    return await fetch(API_URL + 'frase')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        return data;
      })
      .catch(error => {
        console.error('Error:', error);
      });
  }

  getUserBoard() {
    return axios.get(API_URL + 'user', { headers: authHeader() });
  }

  getModeratorBoard() {
    return axios.get(API_URL + 'mod', { headers: authHeader() });
  }

  getAdminBoard() {
    return axios.get(API_URL + 'admin', { headers: authHeader() });
  }
  headers(h: Object): any {
    //const jwt = window.localStorage.getItem('suite@token');
    const headers = {
      'Content-Type': 'application/json',
      'Accept': 'application/json',
       //'Cors': 'no-cors',
      //Authorization: `Bearer ${jwt}`,
      ...h,
    };
    return headers;
  }

}
const Service = new UserService();

export default Service;
